a += a;
console.log(a);   // #1
console.log(a++); // #2
console.log(++a); // #3

var b = 4;
b -= 1;
console.log(b);   // #4
console.log(--b); // #5
console.log(b--); // #6

var c = 3;
c *= 2;
console.log(c);   // #7

c /= 2;
console.log(c);   // #8


